Scripts.OnVerticalScroll:
if (itemHeight == nil) then
	KarmaChatDebug("W2L1: iH == nil!");
	itemHeight = 13;
end;
FauxScrollFrame_OnVerticalScroll(self, offset, itemHeight, KarmaWindow2_UpdateList1);

Scripts.OnClick:
if not KarmaWindow2_PopulateWindow:IsVisible() then
	KarmaWindow2_PopulateWindow:Show();
end

if not KarmaWindow2_FilterList1:Show() then
	KarmaWindow2_FilterList1:Show();
end

Scripts.OnVerticalScroll:
if (itemHeight == nil) then
	KarmaChatDebug("W2L2: iH == nil!");
	itemHeight = 13;
end;
FauxScrollFrame_OnVerticalScroll(self, offset, itemHeight, KarmaWindow2_UpdateList2);
